function textRead ()
{

	document.querySelector('#educationMessage').innerText = textRe.substr(0, iText);
	if (textRe.length > iText){

		iText++;
		setTimeout(textRead, 70);
	}
};

function nextEducation ()
{

	if (textRe.length != iText){

		document.querySelector('#educationMessage').innerText = educationMessage[education];
		iText = textRe.length;

	} else {

		if (education < 2)
		{
			document.querySelector('#educationMessage').innerText = '';
			iText = 0;
			education++;
			textRe = educationMessage[education];
			textRead ();

		} else {
			
			document.querySelector('#educationMessage').style.bottom = '-300px';
			document.querySelector('#backgroundEducation').remove();
		}
	}
}

